var isPlaying = false;

function tryNow(){
   
	if(isPlaying == false){
		isPlaying = true;
		audio_player.currentTime=0;
		audio_player.play();
		$("#tryNowWhite").html('<image src="/static/images/dial_blue.gif" />');
		$("#tryNowBlue").html('<image src="/static/images/dial_white.gif" />');
	}
	else {
		isPlaying = false;
		audio_player.pause();
		$("#tryNowWhite").text("Try Now");
		$("#tryNowBlue").text("Try Now");
	}
}

function audioEnd(){
	audio_player.pause();
	$("#tryNowWhite").text("Try Now");
	$("#tryNowBlue").text("Try Now");
}
 
